package durithon.duri;import android.content.Intent;import android.os.Bundle;import android.support.v7.app.AppCompatActivity;import android.view.View;import android.widget.TextView;public class MapActivity extends AppCompatActivity {    TextView btn_map_history;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_map);        btn_map_history = (TextView) findViewById(R.id.btn_map_history);        btn_map_history.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View view) {                startActivity(new Intent(MapActivity.this, MapHistoryListActivity.class));            }        });    }}